mapper.select:
	select * from
	(select rownum r, t.* from
	(select * from wish 

 	where title like '%'||#{search}||'%' and category=#{category} and kind=#{kind} 


 	where contents like '%'||#{search}||'%' and category=#{category} and kind=#{kind} 


 	where m_id like '%'||#{search}||'%' and category=#{category} and kind=#{kind} 


	order by ref desc, step asc) t)
	where r between #{startRow} and #{lastRow}

mapper.update:
update wish 
set title=#{title}, contents=#{contents}
where num=#{num}
